import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit {

  locationId: number = 0;
  locations:any[] = [];
  fromLocation : string = '';
  toLocation: string = '';
  availableCars : any[] = [];
constructor(private activateRoute:ActivatedRoute , private service:CarService){
  this.activateRoute.params.subscribe(res=>{
    debugger;
    this.locationId = res['locationId'];
    // this.fromLocation = this.locationId;
    this.getCarsFromLocation();
  })
}

ngOnInit(): void {
  this.getAllLocations();
}
getAllLocations(){
  this.service.GetAllLocations().subscribe((res:any)=>{
    this.locations = res.data;
  })
}

getCarsFromLocation(){
  this.service.GetAllCarsByLocation(this.locationId).subscribe((res:any)=>{
    this.availableCars = res.data;
  })
}


}
