import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'zcar';
  isModalVisible: boolean = false;

  openRegister() {
    this.isModalVisible = true;
  }
  closeModal() {
    this.isModalVisible = false;
  }

